$(document).ready(function() {
      $('.sl-js-sticky').scrollspy({
        min: $('ul').offset().top,
        onEnter: function(element, position) {
          $("ul").addClass('sl-is-fixed');
        },
        onLeave: function(element, position) {
          $("ul").removeClass('sl-is-fixed');
        }
      });
    });

